import React, { Component } from 'react'; 
import './index.css';

let time = -7200;
let isPaused = true;
let duration = moment.duration(time * 1000, 'milliseconds');
const interval = 1000;
let flowerButton = document.querySelector('#plantFlower');
let treeButton = document.querySelector('#plantTree');
let isGrowing = true;

export default class Timer extends Component {
    render() {
        return(
            <div>
                <div className="menu-item">
                    <section id="timer-section" class="bg-warning">
                        <h1 className="centered">Timer</h1>
                        <hr aria-hidden="true" />
                        <div className="centered" id="timer" aria-label="Current time elapsed">Click Start!</div>
                        <div className="container">
                            <div className="text-center mb-2">
                                <button type="button" className="btn btn-success mx-2" id="startTimer" aria-label="Start timer" role="button">Continue</button>
                                <button type="button" className="btn btn-warning mx-2" id="pauseTimer" aria-label="Pause timer" role="button">Pause</button>
                                <button type="button" className="btn btn-danger mx-2" id="stopTimer" aria-label="Stop timer" role="button">Stop</button>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        )
    }
}
