import React, { Component } from 'react'; 
import firebase from 'firebase/app';


export default class UserInfo extends Component {

    constructor(props){
        super(props);

        this.state = {
            user: [
                goal = "You haven't saved any goals yet!",
                time = "You haven't saved any study time yet!",
                tree = "You haven't saved any tree preference yet!",
                flower = "You haven't saved any flower preference yet!",
                animal = "You didn't have any animals in your forest :("
            ]
        }
    }

    handleClick = () => {
            let userRef = firebase.database().ref(this.props.userInfo.user);
            userRef.set({
              goal: this.props.userInfo.goal,
              tree: this.props.userInfo.tree,
              flower: this.props.userInfo.flower,
              time: this.props.userInfo.time,
              animal: this.props.userInfo.animal
            })
            .catch(err => console.log(err)); // log any erros
    }

    componentDidMount() {
        if(this.props.userInfo.user === undefined || this.props.userInfo.user === "") {
            return;
        } else {
            let prevGoalRef = firebase.database().ref(this.props.userInfo.user);
            prevGoalRef.on('value', (snapshot) => {
                let value = snapshot.val();
                this.setState({ user: value });
            });
        }
    }


    render() {
        let content;

        if(this.props.userInfo.user === undefined || this.props.userInfo.user === "") {
            content = <div className="alert alert-danger mt-3" role="alert">
                Please Log in first
            </div>
        } else {

            content = 
            <div>
                <h2>WELCOME!</h2>

                <h3>Here is your previous saved goal: </h3>
                <p><strong className="streaks centered">{this.state.user.goal}</strong></p>

                <h3>Here is your previous saved work time: </h3>
                <p><strong className="streaks centered">{this.state.user.time}</strong></p>

                <h3>Here is your previous saved tree preference: </h3>
                <p><strong className="streaks centered">{this.state.user.tree}</strong></p>

                <h3>Here is your previous saved flower preference: </h3>
                <p><strong className="streaks centered">{this.state.user.flower}</strong></p>

                <h3>Here is your previous accomplishment: </h3>
                <p><strong className="streaks centered">{this.state.user.animal}</strong></p>

                <div className="text-center">
                    <button type="button" className="mx-2 mt-1 mb-4 text-center" id="main-btn" aria-label="Submit Personal Preference" role="button" onClick={this.handleClick}>Save your current preference</button>
                </div>
            </div>
        }

        return(
            <div className="col-sm-12 col-md-7 col-lg-7">
                <h2 className="scheduleTitleTop">User Profile</h2>
                <section className="ml-3">
                    {content}
                </section>
            </div>
        )
    }
}
