import React, { Component } from 'react'; 
import './index.css';

export default class DailyGoal extends Component {

    constructor() {
        super();
        this.state = {
            inputGoal: "", 
            inputTime: ""
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleClick = () => {
         this.props.callback(this.state.inputGoal, this.state.inputTime);
    }

    handleChange(event) {
        let name = event.target.name;
        console.log(name);
        let value = event.target.value;
        console.log(value);
        this.setState({
            [name]: value
        });
    } 


    render() {
        return(
            <div>
                <section>
                    <h2 className="scheduleTitle">Daily Goals</h2>
                    <div>
                        <label for="goal_field">It is currently <span className="clock" aria-label="current time">Current Time</span>, what is your goal for today?</label>
                        <textarea type="text" name="inputGoal" className="form-control" placeholder="My goal for today is..." aria-label="Enter your goal for today" value={this.state.inputGoal} onChange={this.handleChange}></textarea>

                        <p>Please enter the amount of time you want to work (Default 2 hours):</p>
                        <input type="text" name="inputTime" placeholder="2 - 9 Hours" aria-label="Enter the amount of time you want to work" value={this.state.inputTime} onChange={this.handleChange}/> 
                    </div>

                    <button type="button" className="btn btn-success mx-2 mt-3" aria-label="Submit Daily Goals" role="button" onClick={this.handleClick}>Submit</button>
                </section>

            </div>
        )
    }

}
