import React, { Component } from 'react'; 
import Navbar from './Nav';
import Main from './Main';
import DailyGoal from './DailyGoal';
import Footer from './Footer';
import User from './User';

import firebase from 'firebase/app';


import { BrowserRouter, Route, Switch } from 'react-router-dom'



class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
        user: "",
        goal: "",
        time: "",
        flower: "",
        flowerImg: "",
        tree: "",
        treeImg: "",
        treeNum: 0,
        flowerNum: 0,
        animal: "",
        didSelectTree: false,
        didSelectFlower: false
    }
}

  updateFirebase = () => {
      let userRef = firebase.database().ref(this.state.user);
      userRef.set( {
        goal: this.state.goal,
        tree: this.state.tree,
        flower: this.state.flower,
        time: this.state.time,
        animal: this.state.animal
      })
      .catch(err => console.log(err)); // log any erros
  }


  enterUser = (user) => {
    this.setState((currentState) => {
      currentState.user = user.replace(/[^a-zA-Z0-9]/g, "");
      return currentState;
    })
  }

  enterGoal = (goal, goalTime) => {
    this.setState((currentState) => {
        currentState.goal = goal;
        currentState.time = goalTime;
        currentState.treeNum = 0;
        currentState.flowerNum = 0;
        return currentState;
    })
}



  enterTree = (tree, treePath) => {
      this.setState((currentState) => {
          currentState.tree = tree;
          currentState.treeImg = treePath;
          currentState.didSelectTree = true;
          return currentState;
      })
  }

  enterFlower = (flower, flowerPath) => {
      this.setState((currentState) => {
          currentState.flower = flower;
          currentState.flowerImg = flowerPath;
          console.log(flower)
          currentState.didSelectFlower = true;
          return currentState;
      })
  }


  addTreeNum = () => {
    this.setState({
        treeNum: this.state.treeNum + 1
    });
  }

  addFlowerNum = () => {
    this.setState({
        flowerNum: this.state.flowerNum + 1
    });
    console.log(this.state.flowerNum);
  }

  render() {
    return (
      <div>


          <BrowserRouter>
            <Navbar/>

              <Switch>

              <Route path='/' exact render={(routerProps) => (
                  <DailyGoal {...routerProps} goalCallback={this.enterGoal} pageCallback={this.updatePage} treeCallback={this.enterTree} flowerCallback={this.enterFlower} userInfo={this.state}  firebaseCallback={this.updateFirebase}/>
              )} />

              <Route path='/content' render={(routerProps) => (
                  <Main {...routerProps} userInfo={this.state} pageCallback={this.updatePage} goalCallback={this.enterGoal} treeCallback={this.enterTree} flowerCallback={this.enterFlower} addTreeNum={this.addTreeNum} addFlowerNum={this.addFlowerNum}/>
              )} />

              <Route path='/user' render={(routerProps) => (
                  <User {...routerProps} userCallback={this.enterUser} firebaseCallback={this.updateFirebase}/>
              )}/>

              </Switch>

            <Footer/>
          </BrowserRouter>

      </div>
    );
  }

}

export default App;
