import React, { Component } from 'react'; 
import PersonalPreference from './PersonalPreference';

import './index.css';

export default class DailyGoal extends Component {

    constructor() {
        super();
        this.state = {
            inputGoal: "", 
            inputTime: "",
            typeWarning: false,
            numWarning: false,
            checkOne: true,
            checkTwo: true
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleClick = () => {

        // Update the tree and flower selection into main.js
        if(this.props.userInfo.didSelectTree === true & this.props.userInfo.didSelectFlower === true) {
            this.setState({
                typeWarning: false
            });

        // Check the number
            if(this.state.inputTime === undefined | this.state.inputTime === "" | this.state.inputTime < 1) {
                // Change the time to default
                this.setState({
                    numWarning: true,
                    checkOne: false
                });
            } else {
                this.setState({
                    numWarning: false
                });
                this.props.goalCallback(this.state.inputGoal, this.state.inputTime);
            }

        } else {
            this.setState({
                typeWarning: true,
                checkTwo: false
            });
        }

        // Update the page
        if (this.state.checkOne === true & this.state.checkTwo === true) {
            this.props.pageCallback("MainContent");
        }
    }

    handleChange(event) {
        // track what user has typed
        let name = event.target.name;
        let value = event.target.value;
        this.setState({
            [name]: value
        });
    } 


    render() {

        let typeWarningMsg;

        // Check wether or not to display the warning message
        if (this.state.typeWarning === false) {
            typeWarningMsg = undefined;
        } else {
            typeWarningMsg = 
            <div className="alert alert-danger" role="alert">
                Please select the required fields in the Personal Preference
            </div>
        }

        // The content of the daily goal page
        return(
            <div>
                <h2 className="scheduleTitle">Daily Goals</h2>
                <section className="col-sm-12 col-md-7 col-lg-8 scheduleForm">
                    <div>
                        <p><strong>Welcome!</strong> It is currently <span className="clock" aria-label="current time">Current Time</span>, what is your goal for today?</p>
                        <textarea type="text" name="inputGoal" className="form-control" placeholder="My goal for today is..." aria-label="Enter your goal for today" value={this.state.inputGoal} onChange={this.handleChange}></textarea>

                        <p>Please enter the amount of hours you want to work <strong>(Default 2 hours)</strong>:</p>
                        <input type="number" name="inputTime" placeholder="Work time..." aria-label="Enter the amount of time you want to work" value={this.state.inputTime} onChange={this.handleChange}/> 
                    </div>
                </section>

                <PersonalPreference treeCallback={this.props.treeCallback} flowerCallback={this.props.flowerCallback}/>

                {typeWarningMsg}

                <div className="text-center">
                    <button type="button" className="mx-2 mt-1 mb-4" id="main-btn" aria-label="Submit Daily Goals" role="button" onClick={this.handleClick}>Submit</button>
                </div>
            </div>
        )
    }

}
