import React, { Component } from 'react'; 
import Main from './main';
import './index.css';

export default class DailyGoal extends Component {

    constructor() {
        super();
    }

    handleClick = () => {
        this.state.callback(this.state.goal, this.state.time);
    }

    render() {
        return(
            <div>
                <section>
                    <h2 className="scheduleTitle">Daily Goals</h2>
                    <div>
                        <label for="goal_field">It is currently <span className="clock" aria-label="current time">Current Time</span>, what is your goal for today?</label>
                        <textarea name="goal" className="form-control" placeholder="My goal for today is..." aria-label="Enter your goal for today" value={this.state.goal}></textarea>

                        <p>Please enter the amount of time you want to work (Default 2 hours):</p>
                        <input type="text" name="Work Hours" aria-label="Enter the amount of time you want to work" placeholder="2 - 9 Hours" value={this.state.time}/> 
                    </div>

                    <button type="button" className="btn btn-success mx-2 mt-3" aria-label="Submit Daily Goals" role="button" onClick={this.handleClick}>Submit</button>
                </section>

            </div>
        )
    }

}
