import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

class TreeCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tree: ""
        }
    }

    handleClick = (event) => {
        // Update the state and change the page content
        event.preventDefault();
        event.stopPropagationâ€©();

        console.log(event.currentTarget);
        this.setState((currState) => {
            currState.tree = event.target.name
        })
         this.props.treeCallback(event.target.name);
    }

    render() {
        return(
            <div className="card" name={this.props.info.name} onClick={this.handleClick}>
                <img src={this.props.info.img} alt={this.props.info.name}/>
                <h4 className="mt-4">{this.props.info.name}</h4>
                <p className="description">{this.props.info.description}</p>
            </div>
        )
    }
}

class TypeList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tree: "",
            flower: ""
        };
    }

    enterTree = (tree) => {
        this.setState((currentState) => {
            currentState.tree = tree;
            console.log(currentState.tree);
            return currentState;
        })
    }

    render() {
        let treeCards = this.props.info.map((eachInfo) => {
            if (eachInfo.type === "Tree") {
                return <TreeCard info={eachInfo} key={eachInfo.id} enterTree={this.enterTree} treeCallback={this.props.treeCallback}/>
            } else {
                return undefined;
            }
        });


        return(
            <div>
                <section className="col-sm-12 col-md-9 col-lg-10 scheduleForm">
                    <h1 className="ml-2 mt-2 mb-3">Here are the trees that you can grow:</h1>
                    <div className="card-pool">
                        {treeCards}
                    </div>
                    <br />
                    <h1 className="ml-2 mt-3 mb-3">Here are the flowers that you can plant:</h1>
                    <div className="card-pool">
                        {/* {flowerCards} */}
                    </div>
                </section>
            </div>

        )
    }

}

export default class Card extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tree: "",
            flower: ""
        };

    }

    handleClick = (event) => {
        // Update the state for the tree and flower selection
        if(this.props.info.type === "Tree") {
            this.setState({
                tree: event.target.name
            })
            console.log(this.state.tree);
            this.props.treeCallback(this.state.tree);
        }

    }

    render() {
        return(
            <div>
                <TypeList info={this.props.info} handleClick={this.handleClick} treeCallback={this.props.treeCallback}/>
            </div>
        )
    }

}
