import React, { Component } from 'react'; 
import DailyGoal from './DailyGoal';
import SideBar from './SideBar';
import './index.css';




export default class Main extends Component {

    constructor(props) {
        super(props);

        this.state = {
            page: "DailyGoal",
            user: {
            goal: "",
            time: "",
            flower: "",
            tree: "",
            time: "",
            animal: ""
            }
        }
    }

    enterGoal = (goal, goalTime) => {
        this.setState((currentState) => {
            currentState.goal = goal;
            currentState.time = goalTime;
            currentState.page = "MainContent";
            return currentState;
        })
    }


    render() {
        let content;

        if (this.props.page === "DailyGoal") {
            content = <DailyGoal callback={this.enterGoal} />
        } else if (this.props.page === "MainContent") {
            content =
                <div>
                    <SideBar/>
                    {/* <Forest />
                    <FocusPlayList /> */}
                </div>
        }
        return(
            <div>
                <main>
                    {content}
                </main>
            </div>
        );
    }
}
