import React, { Component } from 'react'; 
import Timer from 'react-compound-timer';
import './index.css';

export default class ForestTimer extends React.Component {


    render() {
        // let isStart = false;
        // let isPause = false;
        // let isStop = false;
        // let content;

        // if(isStart === false) {
        //     content = 
        //     <div>
        //         <Timer>
        //         <button onClick={resume} className="btn btn-success mx-2">Resume</button>
        //         <button onClick={pause} className="btn btn-warning mx-2">Pause</button>
        //         <button onClick={stop} className="btn btn-danger mx-2">Stop</button>
        //         </Timer>
        //     </div>
        // }

        return (
            <div className="menu-item">
                <section>
                    <h1 className="centered">Countdown</h1>
                    <hr aria-hidden="true" />
                    <Timer 
                    initialTime={10102000}
                    direction="backward">

                    {({ start, resume, pause, stop, reset}) => (
                        <React.Fragment>
                            <div className="centered">
                                <h2 className="streaks">
                                <Timer.Hours />:
                                <Timer.Minutes />:
                                <Timer.Seconds formatValue={value => `${value}`}/>
                                </h2>
                            </div>

                            <br />
                            <div>
                                <button onClick={start} className="btn btn-success mx-2">Start</button>
                                <button onClick={pause} className="btn btn-warning mx-2">Pause</button>
                                <button onClick={resume} className="btn btn-success mx-2">Resume</button>
                                <button onClick={stop} className="btn btn-danger mx-2">Stop</button>
                                <button onClick={reset} className="btn btn-secondary mx-2">Reset</button>
                            </div>
                        </React.Fragment>
                    )}
                    </Timer>
                </section>
            </div>
        );
    }
}
