import React, { Component } from 'react'; 
import Timer from 'react-compound-timer';
import './index.css';

export default class ForestTimer extends React.Component {


    render() {

        let time = this.props.time * 60000
        return (
            <div className="menu-item">
                <section>
                    <h1 className="centered">Countdown</h1>
                    <hr aria-hidden="true" />
                    <Timer 
                    initialTime={time}
                    direction="backward">

                    {({resume, pause, reset}) => (
                        <React.Fragment>
                            <div className="centered">
                                <h1 className="streaks">
                                <Timer.Hours />:
                                <Timer.Minutes />:
                                <Timer.Seconds formatValue={value => `${value}`}/>
                                </h1>
                            </div>

                            <br />
                            <div className="text-center">
                                <button onClick={pause} className="btn btn-warning mx-2">Pause</button>
                                <button onClick={resume} className="btn btn-success mx-2">Resume</button>
                                <button onClick={reset} className="btn btn-secondary mx-2">Restart</button>
                            </div>
                        </React.Fragment>
                    )}
                    </Timer>
                </section>
            </div>
        );
    }
}
