import React, { Component } from 'react'; 

export default class UserInfo extends Component {

    handleClick = () => {
        this.props.firebaseCallback();
    }

    render() {
        return(
            <div className="col-sm-12 col-md-7 col-lg-7">
                <h2 className="scheduleTitleTop">User Profile</h2>
                <section className="ml-3">
                    <button type="button" className="mx-2 mt-1 mb-4" id="main-btn" aria-label="Submit Personal Preference" role="button" onClick={this.handleClick}>Save your current preference</button>
                </section>
            </div>
        )
    }
}
