import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

class TypeCard extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tree: "",
            flower: ""
        };
    }

    render() {
        return(
            <div className="card" onClick={this.handleTreeClick}>
                <img src={this.props.info.img}/>
                <h4 className="mt-4">{this.props.info.name}</h4>
                <p className="description">{this.props.info.description}</p>
            </div>
        )
    }
}


class TypeList extends Component {
    constructor(props) {
        super(props);
    }

    handleClick = () => {
        // Update the state and change the page content
        if(this.props.info.type === "Tree") {
            this.state.tree = this.props.info.name
            console.log(this.state.tree);
            this.props.treeCallback(this.state.tree);
        }

        if(this.props.info.type === "Flower") {
            this.state.flower = this.props.info.name
            console.log(this.state.flower);
            this.props.treeCallback(this.state.flower);
        }

    }

    render() {
        let treeCards = this.props.info.map((eachInfo) => {
            if (eachInfo.type === "Tree") {
                return <TypeCard info={eachInfo} key={eachInfo.id} />
            }
        });

        let flowerCards = this.props.info.map((eachInfo) => {
            if (eachInfo.type === "Flower") {
                return <TypeCard info={eachInfo} key={eachInfo.id} onClick={this.handleFlowerClick}/>
            }
        });

        return(
            <div>
                <section className="col-sm-12 col-md-7 col-lg-8 scheduleForm">
                    <h1>Trees</h1>
                    <div className="card-pool">
                        {treeCards}
                    </div>
                    <br />
                    <h1>Flowers</h1>
                    <div className="card-pool">
                        {flowerCards}
                    </div>
                </section>
            </div>

        )
    }

}

export default class Card extends Component {
    constructor(props) {
        super(props);


    }

    render() {
        return(
            <div>
                <TypeList info={this.props.info}/>
            </div>
        )
    }

}
