import React, { Component } from 'react'; 
import './index.css';
import Navbar from './nav';
import Main from './main';

class App extends Component {

  state = {

      page: "DailyGoal",
      user: {
      goal: "",
      time: "",
      flower: "",
      tree: "",
      treeTime: "",
      animal: ""
    }
  }

  updatePage = (newPage) => {
    this.setState({
      // update the page
      page: newPage
    });
  }

  render() {
    return (
      <div>
        <Navbar />
        <Main user={this.state.user} page={this.state.page} pageCallback={this.updatePage}/>

        <footer>
        </footer>
      </div>
    );
  }

}

export default App;
