import React, { Component } from 'react'; 
import './main';
import './index.css';

export default class DailyGoal extends Component {

    constructor(props) {
        super(props);
        this.state = {inputGoal: "", inputTime: ""}
    }

    handleClick = () => {
        this.props.callback(this.state.inputGoal, this.state.inputTime);
    }

    handleGoalChange(event) {
        let name = event.target.name;
        let value = even.target.value;
        this.setState({[name]: event.target.value})
    }

    handleTimeChange(event) {
        this.setState({inputTime: event.target.value});
    }

    render() {
        return(
            <div>
                <section>
                    <h2 className="scheduleTitle">Daily Goals</h2>
                    <div>
                        <label for="goal_field">It is currently <span className="clock" aria-label="current time">Current Time</span>, what is your goal for today?</label>
                        <textarea name="inputGoal" className="form-control" placeholder="My goal for today is..." aria-label="Enter your goal for today" value={this.state.inputGoal}></textarea>

                        <p>Please enter the amount of time you want to work (Default 2 hours):</p>
                        <input type="text" name="inputTime" aria-label="Enter the amount of time you want to work" placeholder="2 - 9 Hours" value={this.state.inputTime} onChange={this.handleTimeChange}/> 
                    </div>

                    <button type="button" className="btn btn-success mx-2 mt-3" aria-label="Submit Daily Goals" role="button" onClick={this.handleClick}>Submit</button>
                </section>

            </div>
        )
    }

}
