import React, { Component } from 'react'; 
import './index.css';
import Navbar from './nav';
import Main from './main';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
        page: "DailyGoal",
        goal: "",
        time: "",
        flower: "",
        flowerImg: "",
        tree: "",
        treeImg: "",
        treeNum: 0,
        flowerNum: 0,
        animal: "",
        didSelectTree: false,
        didSelectFlower: false
    }
}

  updatePage = (newPage) => {
    this.setState({
      // update the page
      page: newPage
    });
  }

  enterGoal = (goal, goalTime) => {
    this.setState((currentState) => {
        currentState.goal = goal;
        currentState.time = goalTime;
        currentState.page = "MainContent";
        return currentState;

    })
}

  enterTree = (tree, treePath) => {
      this.setState((currentState) => {
          currentState.tree = tree;
          currentState.treeImg = treePath;
          currentState.didSelectTree = true;
          return currentState;
      })
  }

  enterFlower = (flower) => {
      this.setState((currentState, flowerPath) => {
          currentState.flower = flower;
          currentState.flowerImg = flowerPath;
          currentState.didSelectFlower = true;
          return currentState;
      })
  }


  addTreeNum = () => {
        this.setState((currentState) => {
            currentState.treeNum = currentState.treeNum + 1;
            return currentState;
        });
  }

  render() {
    return (
      <div>

        <BrowserRouter>
            <Switch>

            </Switch>
        </BrowserRouter>

        <Main userInfo={this.state} pageCallback={this.updatePage} goalCallback={this.enterGoal} treeCallback={this.enterTree} flowerCallback={this.enterFlower} addTreeNum={this.addTreeNum}/>

        <footer>
        </footer>
      </div>
    );
  }

}

export default App;


if (this.props.userInfo.page === "DailyGoal") {
  content = 
  <div>
      <DailyGoal goalCallback={this.enterGoal} pageCallback={this.updatePage} treeCallback={this.enterTree} flowerCallback={this.enterFlower} userInfo={this.state}/>
  </div>
}
