import React, { Component } from 'react'; 
import DailyGoal from './DailyGoal';
import SideBar from './SideBar';
import ForestContent from './ForestContent';
import FocusPlaylist from './FocusPlaylist';
// import { BrowserRouter as Router, Route, Link} from 'react-router-dom'
import './index.css';




export default class Main extends Component {

    constructor(props) {
        super(props);
    }


    render() {
        let content;

        // <BrowserRouter>
        //     <Switch>
        //         <Route path='/goal' render={(routerProps) => (
        //             <DailyGoal {...routerProps} goalCallback={this.props.goalCallback} pageCallback={this.props.pageCallback} treeCallback={this.props.treeCallback} flowerCallback={this.props.flowerCallback} userInfo={this.props.userInfo}/>
        //         )} />

        //         <Route path='/home' render={(routerProps) => (
        //             <div className="row">
        //                 <SideBar {...routerProps} tree={this.props.userInfo.tree} flower={this.props.userInfo.flower} time={this.props.userInfo.time} addTreeNum={this.props.addTreeNum}/>
        //                 <ForestContent {...routerProps} userInfo={this.props.userInfo} addTreeNum={this.addTreeNum} />
        //                 <FocusPlaylist {...routerProps} />
        //             </div>
        //         )} />
        //     </Switch>
        // </BrowserRouter>

        if (this.props.userInfo.page === "DailyGoal") {
            content = 
            <div>
                <DailyGoal goalCallback={this.props.goalCallback} pageCallback={this.props.pageCallback} treeCallback={this.props.treeCallback} flowerCallback={this.props.flowerCallback} userInfo={this.props.userInfo}/>
            </div>
        } else if (this.props.userInfo.page === "MainContent") {
            content = 
            <div>
                <div className="row">
                    <SideBar tree={this.props.userInfo.tree} flower={this.props.userInfo.flower} time={this.props.userInfo.time} addTreeNum={this.props.addTreeNum}/>
                    <ForestContent userInfo={this.props.userInfo} addTreeNum={this.addTreeNum} />
                    <FocusPlaylist />
                </div>
            </div>
        }


        return(
            <div>
                <main>
                    {content}
                </main>
            </div>
        );
    }
}
