import React, { Component } from 'react'; 
import DailyGoal from './DailyGoal';
import SideBar from './SideBar';
import ForestContent from './ForestContent';
import FocusPlaylist from './FocusPlaylist';
import './index.css';




export default class Main extends Component {

    constructor(props) {
        super(props);
    }


    render() {
        let content;
        console.log("rendering");
        if (this.props.userInfo.page === "DailyGoal") {
            content = 
            <div>
                <DailyGoal goalCallback={this.props.goalCallback} pageCallback={this.props.pageCallback} treeCallback={this.props.treeCallback} flowerCallback={this.props.flowerCallbacklower} userInfo={this.props.userInfo}/>
            </div>
        } else if (this.props.userInfo.page === "MainContent") {
            content = 
            <div>
                <div className="row">
                    <SideBar tree={this.props.userInfo.tree} flower={this.props.userInfo.flower} time={this.props.userInfo.time} addTreeNum={this.props.addTreeNum}/>
                    <ForestContent user={this.state} addTreeNum={this.addTreeNum} />
                    <FocusPlaylist />
                </div>
            </div>
        }


        return(
            <div>
                <main>
                    {content}
                </main>
            </div>
        );
    }
}
