import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

class TypeCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div class="card">
                <img src={this.state.info.img}/>
                <h4>{this.state.info.name}</h4>
                <p className="description">{this.state.info.description}</p>
            </div>
        )
    }
}

export default class Card extends Component {
    constructor(props) {
        super(props);

        this.state = {
            info: []
        }

    }

    componentDidMount() {
        fetch("./treeInfo.json")
        .then((res) => res.json())
        .then((data) => {
            this.setState((currState) => {
                let allTreesFlowers = currState.trees.concat(data);

                return {info: allTreesFlowers};
            }); // end of setState
        }); // end of fetch
    }

    render() {
        return(
            <div>

            </div>
        )
    }

}
