import React, { Component } from 'react'; 
import './index.css';

export default class Timer extends Component {
    render() {
        return(
            <div>
                <div class="menu-item">
                    <section id="timer-section" class="bg-warning">
                        <h1 class="centered">Timer</h1>
                        <hr aria-hidden="true" />
                        <div class="centered" id="timer" aria-label="Current time elapsed">Click Start!</div>
                        <div class="container">
                            <div class="text-center mb-2">
                                <button type="button" class="btn btn-success mx-2" id="startTimer" aria-label="Start timer" role="button">Continue</button>
                                <button type="button" class="btn btn-warning mx-2" id="pauseTimer" aria-label="Pause timer" role="button">Pause</button>
                                <button type="button" class="btn btn-danger mx-2" id="stopTimer" aria-label="Stop timer" role="button">Stop</button>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
        )
    }
}
