import React, { Component } from 'react'; 
import DailyGoal from './DailyGoal';
import PersonalPreference from './PersonalPreference';
import SideBar from './SideBar';
import FocusPlaylist from './FocusPlaylist';
import './index.css';




export default class Main extends Component {

    constructor(props) {
        super(props);

        this.state = {
            page: "DailyGoal",
            user: {
            goal: "",
            time: "",
            flower: "",
            tree: "",
            treeTime: "",
            animal: ""
            }
        }
    }

    enterGoal = (goal, goalTime) => {
        this.setState((currentState) => {
            currentState.goal = goal;
            currentState.time = goalTime;
            currentState.page = "MainContent";
            return currentState;
        })
    }


    render() {
        let content;

        if (this.props.page === "DailyGoal") {
            content = 
            <div>
                <DailyGoal callback={this.enterGoal} pageCallback={this.props.pageCallback}/>
                {/* <PersonalPreference /> */}
            </div>
        } else if (this.props.page === "MainContent") {
            content = 
            <div>
                <SideBar/>
                <FocusPlaylist />
            </div>
        }
        return(
            <div>
                <main>
                    {content}
                </main>
            </div>
        );
    }
}
