import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

class TypeCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div className="card">
                <img src={this.props.info.img}/>
                <h4>{this.props.info.name}</h4>
                <p className="description">{this.props.info.description}</p>
            </div>
        )
    }
}

class TypeList extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let cards = this.props.info.map((eachInfo) => {
            return <TypeCard info={eachInfo}/>
        });

        return(
            <div className="card-pool">
                {cards}
            </div>
        )
    }

}

export default class Card extends Component {
    constructor(props) {
        super(props);


    }

    render() {
        return(
            <div>
                <TypeList info={this.props.info}/>
            </div>
        )
    }

}
