import React, { Component } from 'react'; 
import './index.css';
import Navbar from './nav';
// import Main from './main';
import DailyGoal from './DailyGoal';

import { BrowserRouter, Route, Switch, Link} from 'react-router-dom'



class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
        page: "DailyGoal",
        goal: "",
        time: "",
        flower: "",
        flowerImg: "",
        tree: "",
        treeImg: "",
        treeNum: 0,
        flowerNum: 0,
        animal: "",
        didSelectTree: false,
        didSelectFlower: false
    }
}

  updatePage = (newPage) => {
    this.setState({
      // update the page
      page: newPage
    });
  }

  enterGoal = (goal, goalTime) => {
    this.setState((currentState) => {
        currentState.goal = goal;
        currentState.time = goalTime;
        currentState.page = "MainContent";
        return currentState;

    })
}

  enterTree = (tree, treePath) => {
      this.setState((currentState) => {
          currentState.tree = tree;
          currentState.treeImg = treePath;
          currentState.didSelectTree = true;
          return currentState;
      })
  }

  enterFlower = (flower) => {
      this.setState((currentState, flowerPath) => {
          currentState.flower = flower;
          currentState.flowerImg = flowerPath;
          currentState.didSelectFlower = true;
          return currentState;
      })
  }


  addTreeNum = () => {
        this.setState({
            treeNum: this.state.treeNum + 1
        });
  }

  render() {
    return (
      <div>
          <Navbar/>


          <BrowserRouter>
              <Switch>

              <Route path='/' component={App}/>

              <Route path='/content' component={DailyGoal}/>

              </Switch>
          </BrowserRouter>


        <footer>
        </footer>
      </div>
    );
  }

}

export default App;
