import React, { Component } from 'react'; 
import DailyGoal from './DailyGoal';
import SideBar from './SideBar';
import ForestContent from './ForestContent';
import FocusPlaylist from './FocusPlaylist';
import './index.css';




export default class Main extends Component {

    constructor(props) {
        super(props);

        this.state = {
            page: "DailyGoal",
            goal: "",
            time: "",
            flower: "",
            flowerImg: "",
            tree: "",
            treeImg: "",
            treeNum: 0,
            flowerNum: 0,
            animal: "",
            didSelectTree: false,
            didSelectFlower: false
        }
    }

    enterGoal = (goal, goalTime) => {
        this.setState((currentState) => {
            currentState.goal = goal;
            currentState.time = goalTime;
            currentState.page = "MainContent";
            return currentState;
        })
    }

    enterTree = (tree, treePath) => {
        this.setState((currentState) => {
            currentState.tree = tree;
            currentState.treeImg = treePath;
            currentState.didSelectTree = true;
            return currentState;
        })
    }

    enterFlower = (flower) => {
        this.setState((currentState, flowerPath) => {
            currentState.flower = flower;
            currentState.flowerImg = flowerPath;
            currentState.didSelectFlower = true;
            return currentState;
        })
    }

    addTreeNum = () => {
        this.setState((currentState) => {
            currentState.treeNum = Number(currentState.treeNum) + 1;
            currentState.canGrowTree = false;
            return currentState;
        })
    }


    render() {
        let content;
        if (this.props.page === "DailyGoal") {
            content = 
            <div>
                <DailyGoal goalCallback={this.enterGoal} pageCallback={this.props.pageCallback} treeCallback={this.enterTree} flowerCallback={this.enterFlower} userInfo={this.state}/>
            </div>
        } else if (this.props.page === "MainContent") {
            content = 
            <div>
                <div className="row">
                    <SideBar tree={this.state.tree} flower={this.state.flower} time={this.state.time} addTreeNum={this.addTreeNum}/>
                    <ForestContent user={this.state} addTreeNum={this.addTreeNum} />
                    <FocusPlaylist />
                </div>
            </div>
        }


        return(
            <div>
                <main>
                    {content}
                </main>
            </div>
        );
    }
}
