import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

export default class Card extends Component {
    constructor(props) {
        super(props);

        this.state = {
            trees: [],
            flowers: []
        }

    }

    componentDidMount() {
        fetch("./treeInfo.json")
        .then((res) => res.json())
        .then((data) => {
            this.setState((currState) => {
                let allTrees = currState.trees.concat(data);

                return {trees: allTrees};
            }); // end of setState
        }); // end of fetch
    }
}
