import React, { Component } from 'react'; 

// css
import './index.css';

// fetch
import 'whatwg-fetch';

class FlowerCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div className="card">
                <img src={this.props.info.img}/>
                <h4>{this.props.info.name}</h4>
                <p className="description">{this.props.info.description}</p>
            </div>
        )
    }
}

class TreeCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return(
            <div className="card">
                <img src={this.props.info.img}/>
                <h4>{this.props.info.name}</h4>
                <p className="description">{this.props.info.description}</p>
            </div>
        )
    }
}

class TypeList extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let treeCards = this.props.info.map((eachInfo) => {
            if (eachInfo.type === "Tree") {
                return <TypeCard info={eachInfo} key={eachInfo.id}/>
            }
        });

        let flowerCards = this.props.info.map((eachInfo) => {
            if (eachInfo.type === "Flower") {
                return <TypeCard info={eachInfo} key={eachInfo.id}/>
            }
        });

        return(
            <div className="card-pool">
                <p>Trees</p>
                {treeCards}
                <p>Flowers</p>
                {flowerCards}
            </div>
        )
    }

}

export default class Card extends Component {
    constructor(props) {
        super(props);


    }

    render() {
        return(
            <div>
                <TypeList info={this.props.info}/>
            </div>
        )
    }

}
